import { Router } from "express";
import PostController from "../controllers/PostController";
import PostMiddleware from "../middlewares/PostMiddleware";

const router = Router();

router.get("/", PostController.getAllPosts);
router.get("/:id", PostMiddleware.validateParams, PostController.showPost);
router.post("/", PostMiddleware.addPost, PostController.addPost);
router.put("/:id", PostMiddleware.editPost, PostController.editPost);
router.delete("/:id", PostMiddleware.validateParams, PostController.deletePost);

router.post("/:id/rate", PostMiddleware.addRating, PostController.addRating);
router.get("/:id/rate", PostMiddleware.validateParams, PostController.getRating);

export default router;

import "express-async-errors";
import PostService from "../services/PostService";
import Util from "../utils/Utils";

const util = new Util();

class PostController {
  static async getAllPosts(req, res) {
    const allPosts = await PostService.getAllPosts();

    util.setSuccess(200, "Posts retrieved", allPosts);
    util.send(res);
    return;
  }

  static async addPost(req, res) {
    const newPost = await PostService.addPost(req.body);

    util.setSuccess(201, "Post created", newPost);
    util.send(res);
    return;
  }

  static async editPost(req, res) {
    const { id } = req.params;
    const post = await PostService.editPost(id, req.body);

    if (post == undefined) util.setError(404, `Cannot find post with the id ${id}`);
    else util.setSuccess(200, "Post updated", post);

    util.send(res);
    return;
  }

  static async showPost(req, res) {
    const { id } = req.params;
    const post = await PostService.getPost(id);

    if (post == undefined) util.setError(404, `Cannot find post with the id ${id}`);
    else util.setSuccess(200, "Post retrieved", post);

    util.send(res);
    return;
  }

  static async deletePost(req, res) {
    const { id } = req.params;
    const post = await PostService.deletePost(id);

    if (post == undefined) util.setError(404, `Cannot find post with the id ${id}`);
    else util.setSuccess(200, "Posts deleted", {});

    util.send(res);
    return;
  }

  static async addRating(req, res) {
    const { id } = req.params;
    const post = await PostService.ratePost(id, req.body.rating);

    if (post == undefined) util.setError(404, `Cannot find post with the id ${id}`);
    else util.setSuccess(200, "Rating added to post", post);

    util.send(res);
    return;
  }

  static async getRating(req, res) {
    const { id } = req.params;
    const post = await PostService.getAvgPostRating(id);

    if (post == undefined) util.setError(404, `Cannot find post with the id ${id}`);
    else util.setSuccess(200, "Post rating retrieved", post);

    util.send(res);
    return;
  }
}

export default PostController;

// import 'express-async-errors'
import DB from "../models";

class PostService {
  static async ratePost(id, postRating) {
    const postToRate = await DB.Post.findByPk(id);

    if (postToRate) {
      const new_total_ratings = postToRate.number_of_ratings + 1;
      const previous_weigh = postToRate.average_rating * postToRate.number_of_ratings
      const new_average_rating = (previous_weigh + postRating) / new_total_ratings;

      return await postToRate.update({
        average_rating: new_average_rating,
        number_of_ratings: new_total_ratings,
      });
    }

    return undefined;
  }

  static async getAllPosts() {
    return await DB.Post.findAll();
  }

  static async getPost(id) {
    const post = await DB.Post.findByPk(id);
    return post ? post : undefined;
  }

  static async addPost(newPost) {
    return await DB.Post.create(newPost);
  }

  static async editPost(id, editPost) {
    const postToEdit = await DB.Post.findByPk(id);
    return postToEdit ? await postToEdit.update({ ...editPost }) : undefined;
  }

  static async getAvgPostRating(id) {
    const post = await DB.Post.findByPk(id);
    return post ? post.average_rating : undefined;
  }

  static async deletePost(id) {
    const postToDelete = await DB.Post.findByPk(id);
    return postToDelete ? await postToDelete.destroy() : undefined;
  }
}

export default PostService;

