module.exports = (sequelize, DataTypes) => {
  const Post = sequelize.define('Post', {
    description: {
      type: DataTypes.TEXT,
      allowNull: false,
    },
    average_rating: {
      type: DataTypes.FLOAT,
      allowNull: false,
